import API from './api';

// Admin Product Management
export const adminProductAPI = {
  create: (productData) => API.post('/admin/products', productData),
  update: (id, productData) => API.put(`/admin/products/${id}`, productData),
  delete: (id) => API.delete(`/admin/products/${id}`),
};

// Admin Order Management
export const adminOrderAPI = {
  getAll: () => API.get('/admin/orders'),
  updateStatus: (id, statusData) => API.put(`/admin/orders/${id}`, statusData),
};

// Admin Dashboard
export const adminDashboardAPI = {
  getStats: () => API.get('/admin/dashboard'),
};

export default {
  adminProductAPI,
  adminOrderAPI,
  adminDashboardAPI
};
