import React, { useState, useEffect } from 'react';
import { getDashboardStats } from '../../../services/adminApi.js';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState([]);
  const [recentActivity, setRecentActivity] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const data = await getDashboardStats();
        
        // Transform backend data to frontend format
        const transformedStats = [
          { title: 'Total Products', value: data.stats.totalProducts.toString(), icon: 'üõçÔ∏è', color: '#3498db' },
          { title: 'Total Orders', value: data.stats.totalOrders.toString(), icon: 'üì¶', color: '#2ecc71' },
          { title: 'Total Users', value: data.stats.totalUsers.toString(), icon: 'üë•', color: '#9b59b6' },
          { title: 'Revenue', value: `$${data.stats.revenue}`, icon: 'üí∞', color: '#f39c12' }
        ];
        
        setStats(transformedStats);
        setRecentActivity(data.recentActivity || []);
      } catch (error) {
        setError('Failed to load dashboard data');
        console.error('Error fetching dashboard:', error);
        
        // Fallback to mock data if backend fails
        setStats([
          { title: 'Total Products', value: '152', icon: 'üõçÔ∏è', color: '#3498db' },
          { title: 'Total Orders', value: '89', icon: 'üì¶', color: '#2ecc71' },
          { title: 'Total Users', value: '1,234', icon: 'üë•', color: '#9b59b6' },
          { title: 'Revenue', value: '$12,456', icon: 'üí∞', color: '#f39c12' }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="admin-dashboard">
      <div className="stats-grid">
        {stats.map((stat, index) => (
          <div key={index} className="stat-card" style={{ borderLeftColor: stat.color }}>
            <div className="stat-icon" style={{ backgroundColor: stat.color }}>
              {stat.icon}
            </div>
            <div className="stat-info">
              <h3>{stat.value}</h3>
              <p>{stat.title}</p>
            </div>
          </div>
        ))}
      </div>

      <div className="dashboard-content">
        <div className="recent-activity">
          <h2>Recent Activity</h2>
          <div className="activity-list">
            {recentActivity.map((activity, index) => (
              <div key={index} className="activity-item">
                <span className="activity-icon">
                  {activity.type === 'product_added' && 'üõçÔ∏è'}
                  {activity.type === 'order_shipped' && 'üì¶'}
                  {activity.type === 'user_registered' && 'üë§'}
                </span>
                <div className="activity-details">
                  <p>{activity.message}</p>
                  <span className="activity-time">{activity.time}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
