import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProductCard from '../../components/ProductCard/ProductCard';

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchHomeData();
  }, []);

  const fetchHomeData = async () => {
    try {
      // Pending replace with actual API call
      const mockFeaturedProducts = [
        {
          _id: '19',
          name: 'Wireless Bluetooth Headphones',
          images: ['https://via.placeholder.com/300x300/007bff/ffffff?text=Headphones'],
          description: 'Premium wireless headphones with noise cancellation',
          price: 199.99,
          category: 'Electronics',
          brand: 'Sony',
          rating: 4.8,
          numReviews: 156,
          countInStock: 25,
          featured: true
        },
        {
          _id: '20',
          name: 'Smart Fitness Watch',
          images: ['https://via.placeholder.com/300x300/28a745/ffffff?text=Fitness+Watch'],
          description: 'Track your fitness with this advanced smartwatch',
          price: 299.99,
          category: 'Electronics',
          brand: 'Fitbit',
          rating: 4.6,
          numReviews: 234,
          countInStock: 18,
          featured: true
        },
        {
          _id: '21',
          name: 'Designer Handbag',
          images: ['https://via.placeholder.com/300x300/fd7e14/ffffff?text=Designer+Handbag'],
          description: 'Luxury designer handbag with premium leather',
          price: 399.99,
          category: 'Women',
          brand: 'Gucci',
          rating: 4.9,
          numReviews: 89,
          countInStock: 8,
          featured: true
        },
        {
          _id: '22',
          name: 'Gaming Laptop',
          images: ['https://via.placeholder.com/300x300/dc3545/ffffff?text=Gaming+Laptop'],
          description: 'High-performance gaming laptop with RTX graphics',
          price: 1499.99,
          category: 'Electronics',
          brand: 'ASUS',
          rating: 4.7,
          numReviews: 312,
          countInStock: 12,
          featured: true
        }
      ];

      const mockCategories = [
        {
          id: 1,
          name: 'Men\'s Fashion',
          image: 'https://via.placeholder.com/400x300/343a40/ffffff?text=Men+Fashion',
          count: 156,
          link: '/men'
        },
        {
          id: 2,
          name: 'Women\'s Fashion',
          image: 'https://via.placeholder.com/400x300/e83e8c/ffffff?text=Women+Fashion',
          count: 234,
          link: '/women'
        },
        {
          id: 3,
          name: 'Kids & Toys',
          image: 'https://via.placeholder.com/400x300/20c997/ffffff?text=Kids+Toys',
          count: 189,
          link: '/kids'
        },
        {
          id: 4,
          name: 'Electronics',
          image: 'https://via.placeholder.com/400x300/007bff/ffffff?text=Electronics',
          count: 312,
          link: '/products?category=electronics'
        }
      ];

      setFeaturedProducts(mockFeaturedProducts);
      setCategories(mockCategories);
    } catch (error) {
      console.error('Error fetching home data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div style={styles.loading}>Loading...</div>;
  }

  return (
    <div style={styles.container}>
      {/* Hero Section */}
      <section style={styles.hero}>
        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>Welcome to The Digital Bazar</h1>
          <p style={styles.heroSubtitle}>
            Discover amazing products at unbeatable prices. Shop the latest trends in fashion, electronics, and more.
          </p>
          <Link to="/products" style={styles.heroButton}>
            Shop Now
          </Link>
        </div>
        <div style={styles.heroImage}>
          <img 
            src="https://via.placeholder.com/600x400/6c757d/ffffff?text=Hero+Image" 
            alt="Shopping"
            style={styles.heroImg}
          />
        </div>
      </section>

      {/* Categories Section */}
      <section style={styles.section}>
        <h2 style={styles.sectionTitle}>Shop by Category</h2>
        <div style={styles.categoriesGrid}>
          {categories.map(category => (
            <Link key={category.id} to={category.link} style={styles.categoryCard}>
              <img 
                src={category.image} 
                alt={category.name}
                style={styles.categoryImage}
              />
              <div style={styles.categoryContent}>
                <h3 style={styles.categoryName}>{category.name}</h3>
                <p style={styles.categoryCount}>{category.count} products</p>
              </div>
            </Link>
          ))}
        </div>
      </section>

      {/* Featured Products Section */}
      <section style={styles.section}>
        <h2 style={styles.sectionTitle}>Featured Products</h2>
        <div style={styles.productsGrid}>
          {featuredProducts.map(product => (
            <ProductCard key={product._id} product={product} />
          ))}
        </div>
        <div style={styles.viewAllContainer}>
          <Link to="/products" style={styles.viewAllButton}>
            View All Products ‚Üí
          </Link>
        </div>
      </section>

      {/* Banner Section */}
      <section style={styles.banner}>
        <div style={styles.bannerContent}>
          <h2 style={styles.bannerTitle}>Summer Sale Up to 50% Off</h2>
          <p style={styles.bannerText}>
            Don't miss out on our biggest sale of the season. Limited time offer!
          </p>
          <Link to="/products?category=sale" style={styles.bannerButton}>
            Shop Sale
          </Link>
        </div>
      </section>

      {/* Features Section */}
