import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { productAPI } from '../../services/api';
import './Home.css';

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchFeaturedProducts();
  }, []);

  const fetchFeaturedProducts = async () => {
    try {
      setLoading(true);
      const response = await productAPI.getFeatured();
      setFeaturedProducts(response.data.data);
    } catch (err) {
      setError('Failed to load featured products');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const categories = [
    {
      name: 'Men',
      path: '/men',
      image: 'https://images.unsplash.com/photo-1617137968427-85924c800a22?w=400&h=300&fit=crop',
      description: 'Stylish clothing for men'
    },
    {
      name: 'Women',
      path: '/women',
      image: 'https://images.unsplash.com/photo-1485231183945-fffde7cb34eb?w=400&h=300&fit=crop',
      description: 'Fashionable outfits for women'
    },
    {
      name: 'Kids',
      path: '/kids',
      image: 'https://images.unsplash.com/photo-1554342886-4c1fba44e0a6?w=400&h=300&fit=crop',
      description: 'Cute clothes for children'
    }
  ];

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-background">
          <img 
            src="https://images.unsplash.com/photo-1441986300917-64674bd600d8?w=1200&h=600&fit=crop" 
            alt="Fashion Collection" 
            className="hero-image"
          />
          <div className="hero-overlay"></div>
        </div>
        <div className="hero-content">
          <h1 className="hero-title">Summer Collection 2024</h1>
          <p className="hero-subtitle">Discover the latest trends in fashion for everyone</p>
          <Link to="/women" className="cta-button">Shop Now</Link>
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories-section">
        <div className="container">
          <h2 className="section-title">Shop by Category</h2>
          <p className="section-subtitle">Explore our wide range of fashion categories</p>
          <div className="categories-grid">
            {categories.map((category, index) => (
              <Link to={category.path} key={index} className="category-card">
                <div className="category-image">
                  <img 
                    src={category.image} 
                    alt={category.name}
                    className="category-img"
                  />
                  <div className="category-overlay">
                    <h3 className="category-name">{category.name}</h3>
                    <p className="category-description">{category.description}</p>
                    <span className="shop-now">Shop Now →</span>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* Featured Products */}
      <section className="featured-section">
        <div className="container">
          <h2 className="section-title">Featured Products</h2>
          <p className="section-subtitle">Check out our best selling items</p>
          
          {loading ? (
            <div className="loading-grid">
              {[1, 2, 3].map(item => (
                <div key={item} className="product-card loading">
                  <div className="product-image loading"></div>
                  <div className="product-info">
                    <div className="loading-line"></div>
                    <div className="loading-line short"></div>
                    <div className="loading-line shorter"></div>
                  </div>
                </div>
              ))}
            </div>
          ) : error ? (
            <div className="error-message">
              <p>{error}</p>
              <button onClick={fetchFeaturedProducts} className="retry-button">
                Retry
              </button>
            </div>
          ) : (
            <div className="products-grid">
              {featuredProducts.map(product => (
                <div key={product._id} className="product-card">
                  <div className="product-image">
                    <img 
                      src={product.images[0]?.url} 
                      alt={product.name}
                      className="product-img"
                    />
                    {product.originalPrice > product.price && (
                      <span className="discount-badge">
                        {Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100)}% OFF
                      </span>
                    )}
                  </div>
                  <div className="product-info">
                    <span className="product-category">{product.subcategory}</span>
                    <h3 className="product-name">{product.name}</h3>
                    
                    <div className="product-rating">
                      <div className="stars">
                        {'★'.repeat(Math.floor(product.rating.average))}
                        {'☆'.repeat(5 - Math.floor(product.rating.average))}
                      </div>
                      <span className="rating-text">({product.rating.count})</span>
                    </div>

                    <div className="product-pricing">
                      <span className="current-price">${product.price}</span>
                      {product.originalPrice > product.price && (
                        <span className="original-price">${product.originalPrice}</span>
                      )}
                    </div>

                    <Link to={`/product/${product._id}`} className="view-details-btn">
                      View Details
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default Home;
