import React, { useState, useEffect } from 'react';
import ProductCard from '../../components/ProductCard/ProductCard';

const Men = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMenProducts();
  }, []);

  const fetchMenProducts = async () => {
    try {
      // Replace with actual API call if backend is ready
      const menProducts = [
        {
          _id: '1',
          name: "Men's Running Shoes",
          images: ['https://images.unsplash.com/photo-1618354695941-1827e9481d44?w=500&h=500&fit=crop'],
          description: 'Lightweight running shoes with superior cushioning for men',
          price: 129.99,
          category: 'Men',
          brand: 'Nike',
          rating: 4.3,
          numReviews: 28,
          countInStock: 25
        },
        {
          _id: '2',
          name: "Men's Casual Shirt",
          images: ['https://images.unsplash.com/photo-1593032465171-8b39f3d0e2f6?w=500&h=500&fit=crop'],
          description: 'Comfortable casual shirt made from premium cotton',
          price: 29.99,
          category: 'Men',
          brand: 'H&M',
          rating: 4.1,
          numReviews: 23,
          countInStock: 50
        },
        {
          _id: '3',
          name: "Men's Jeans",
          images: ['https://images.unsplash.com/photo-1586864388554-b99ee1d61135?w=500&h=500&fit=crop'],
          description: 'Slim fit jeans with stretch fabric for comfort',
          price: 59.99,
          category: 'Men',
          brand: "Levi's",
          rating: 4.4,
          numReviews: 45,
          countInStock: 30
        },
        {
          _id: '4',
          name: "Men's Jacket",
          images: ['https:https://images.unsplash.com/photo-1715608720717-ac3d1b638e44?q=80&w=685&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D'],
          description: 'Waterproof jacket with insulation for cold weather',
          price: 89.99,
          category: 'Men',
          brand: 'North Face',
          rating: 4.6,
          numReviews: 32,
          countInStock: 15
        },
        {
          _id: '5',
          name: "Men's Watch",
          images: ['https://images.unsplash.com/photo-1571689938155-3e5a3c71f6a2?w=500&h=500&fit=crop'],
          description: 'Classic leather strap watch for men',
          price: 149.99,
          category: 'Men',
          brand: 'Fossil',
          rating: 4.5,
          numReviews: 67,
          countInStock: 12
        },
        {
          _id: '6',
          name: "Men's Backpack",
          images: ['https://images.unsplash.com/photo-1593032465264-3d4fbc83f8f7?w=500&h=500&fit=crop'],
          description: 'Durable backpack for daily use with laptop compartment',
          price: 49.99,
          category: 'Men',
          brand: 'Adidas',
          rating: 4.2,
          numReviews: 38,
          countInStock: 20
        }
      ];

      setProducts(menProducts);
    } catch (error) {
      console.error("Error fetching men's products:", error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div style={styles.loading}>Loading men's collection...</div>;
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Men's Collection</h1>
        <p style={styles.subtitle}>Discover our latest men's fashion and accessories</p>
      </div>

      <div style={styles.productsGrid}>
        {products.map(product => (
          <ProductCard key={product._id} product={product} />
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '1400px',
    margin: '0 auto',
    padding: '2rem'
  },
  header: {
    textAlign: 'center',
    marginBottom: '3rem'
  },
  title: {
    fontSize: '2.5rem',
    color: '#333',
    marginBottom: '0.5rem'
  },
  subtitle: {
    fontSize: '1.1rem',
    color: '#666',
    maxWidth: '600px',
    margin: '0 auto'
  },
  productsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
    gap: '2rem'
  },
  loading: {
    textAlign: 'center',
    padding: '4rem',
    fontSize: '1.2rem'
  }
};

export default Men;
