import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../../contexts/CartContext';
import { useAuth } from '../../contexts/AuthContext';
import { PayPalScriptProvider, PayPalButtons } from '@paypal/react-paypal-js';
import './Checkout.css'; // Assuming you intend to use this CSS file for styling

const Checkout = () => {
  const { items, getCartTotal, clearCart } = useCart();
  const { user } = useAuth();
  const navigate = useNavigate();

  const [orderProcessing, setOrderProcessing] = useState(false);
  const [shippingInfo, setShippingInfo] = useState({
    address: '',
    city: '',
    postalCode: '',
    country: ''
  });

  const subtotal = getCartTotal();
  const shipping = 5.00;
  const tax = subtotal * 0.1;
  // Make sure total calculation is rounded for display/API, though toFixed is used later
  const total = subtotal + shipping + tax;

  const handleShippingChange = (e) => {
    setShippingInfo({
      ...shippingInfo,
      [e.target.name]: e.target.value
    });
  };

  const createOrder = async () => {
    // A simple validation check before attempting to create the order
    if (!shippingInfo.address || !shippingInfo.city || !shippingInfo.postalCode || !shippingInfo.country) {
      alert('Please fill out all shipping information before proceeding.');
      throw new Error('Missing shipping information');
    }
    
    try {
      const response = await fetch('/api/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('userToken')}`
        },
        body: JSON.stringify({
          orderItems: items,
          shippingAddress: shippingInfo,
          paymentMethod: 'paypal',
          itemsPrice: subtotal,
          taxPrice: tax,
          shippingPrice: shipping,
          totalPrice: total
        })
      });

      const order = await response.json();
      
      if (!response.ok) {
        throw new Error(order.message || 'Failed to create order');
      }

      // PayPal requires an order ID string to proceed
      return order._id; 
    } catch (error) {
      console.error('Error creating order:', error);
      alert('Could not create order. Please try again.');
      throw error;
    }
  };

  const onApprove = async (data, actions) => {
    setOrderProcessing(true);
    
    try {
      // Execute the payment (this step is often handled by a server function in a real app)
      // For this frontend implementation, we'll assume the client-side PayPal order ID is enough
      // to finalize the order payment status on the backend.
      
      const response = await fetch(`/api/orders/${data.orderID}/pay`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('userToken')}`
        },
        body: JSON.stringify({
          paymentResult: {
            id: data.orderID, // PayPal Order ID
            status: 'COMPLETED',
            update_time: new Date().toISOString(),
            email_address: data.payer.email_address
          }
        })
      });

      const result = await response.json();

      if (response.ok) {
        clearCart();
        navigate(`/order-success/${result._id}`);
      } else {
        throw new Error('Payment failed on server side update.');
      }
    } catch (error) {
      console.error('Payment error:', error);
      alert('Payment failed. Please try again.');
    } finally {
      setOrderProcessing(false);
    }
  };

  const onError = (err) => {
    console.error('PayPal Checkout Error:', err);
    alert('There was an error with your payment. Please try again.');
  };

  // Redirect users if not logged in or cart is empty
  if (!user) {
    navigate('/login');
    return null;
  }

  if (items.length === 0) {
    navigate('/cart');
    return null;
  }

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Checkout</h1>
      
      <div style={styles.checkoutContent}>
        {/* Shipping Information */}
        <div style={styles.shippingSection}>
          <h2 style={styles.sectionTitle}>Shipping Information</h2>
          {/* Note: In a real app, this form should likely submit a handler function */}
          <form style={styles.shippingForm} onSubmit={(e) => e.preventDefault()}> 
            <div style={styles.formGroup}>
              <label>Address</label>
              <input
                type="text"
                name="address"
                value={shippingInfo.address}
                onChange={handleShippingChange}
                required
                style={styles.input}
              />
            </div>
            
            <div style={styles.formRow}>
              <div style={styles.formGroup}>
                <label>City</label>
                <input
                  type="text"
                  name="city"
                  value={shippingInfo.city}
                  onChange={handleShippingChange}
                  required
                  style={styles.input}
                />
              </div>
              
              <div style={styles.formGroup}>
                <label>Postal Code</label>
                <input
                  type="text"
                  name="postalCode"
                  value={shippingInfo.postalCode}
                  onChange={handleShippingChange}
                  required
                  style={styles.input}
                />
              </div>
            </div>
            
            <div style={styles.formGroup}>
              <label>Country</label>
              <input
                type="text"
                name="country"
                value={shippingInfo.country}
                onChange={handleShippingChange}
                required
                style={styles.input}
              />
            </div>
          </form>
        </div>

        {/* Order Summary */}
        <div style={styles.orderSummary}>
          <h2 style={styles.sectionTitle}>Order Summary</h2>
          
          <div style={styles.orderItems}>
            {items.map(item => (
              <div key={item._id} style={styles.orderItem}>
                <img 
                  src={item.image} 
                  alt={item.name}
                  style={styles.itemImage}
                />
                <div style={styles.itemDetails}>
                  <h4 style={styles.itemName}>{item.name}</h4>
                  <p style={styles.itemPrice}>
                    ${item.price.toFixed(2)} x {item.quantity}
                  </p>
                </div>
                <div style={styles.itemTotal}>
                  ${(item.price * item.quantity).toFixed(2)}
                </div>
              </div>
            ))}
          </div>

          <div style={styles.priceBreakdown}>
            <div style={styles.priceRow}>
              <span>Subtotal:</span>
              <span>${subtotal.toFixed(2)}</span>
            </div>
            <div style={styles.priceRow}>
              <span>Shipping:</span>
              <span>${shipping.toFixed(2)}</span>
            </div>
            <div style={styles.priceRow}>
              <span>Tax (10%):</span>
              <span>${tax.toFixed(2)}</span>
            </div>
            <div style={{...styles.priceRow, ...styles.total}}>
              <span>Total:</span>
              <span>${total.toFixed(2)}</span>
            </div>
          </div>

          {/* PayPal Button */}
          <div style={styles.paymentSection}>
            <h3 style={styles.paymentTitle}>Payment Method</h3>
            <p style={styles.paymentNote}>Pay securely with PayPal</p>
            
            <PayPalScriptProvider 
              options={{ 
                // IMPORTANT: Replace "test" with a real client ID in production
                "client-id": process.env.REACT_APP_PAYPAL_CLIENT_ID || "test",
                currency: "USD"
              }}
            >
              <div style={styles.paypalButton}>
                <PayPalButtons
                  style={{ 
                    layout: "vertical",
                    color: "gold",
                    shape: "rect",
                    label: "paypal"
                  }}
                  createOrder={createOrder}
                  onApprove={onApprove}
                  onError={onError}
                  // Disable the button until shipping info is filled out
                  disabled={orderProcessing || !shippingInfo.address || !shippingInfo.city || !shippingInfo.postalCode || !shippingInfo.country}
                />
              </div>
            </PayPalScriptProvider>
            
            {orderProcessing && (
              <div style={styles.processing}>
                Processing your order...
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

