import React from 'react';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const stats = [
    { title: 'Total Products', value: '152', icon: 'üõçÔ∏è', color: '#3498db' },
    { title: 'Total Orders', value: '89', icon: 'üì¶', color: '#2ecc71' },
    { title: 'Total Users', value: '1,234', icon: 'üë•', color: '#9b59b6' },
    { title: 'Revenue', value: '$12,456', icon: 'üí∞', color: '#f39c12' }
  ];

  return (
    <div className="admin-dashboard">
      <div className="stats-grid">
        {stats.map((stat, index) => (
          <div key={index} className="stat-card" style={{ borderLeftColor: stat.color }}>
            <div className="stat-icon" style={{ backgroundColor: stat.color }}>
              {stat.icon}
            </div>
            <div className="stat-info">
              <h3>{stat.value}</h3>
              <p>{stat.title}</p>
            </div>
          </div>
        ))}
      </div>

      <div className="dashboard-content">
        <div className="recent-activity">
          <h2>Recent Activity</h2>
          <div className="activity-list">
            <div className="activity-item">
              <span className="activity-icon">üõçÔ∏è</span>
              <div className="activity-details">
                <p>New product added: "Wireless Headphones"</p>
                <span className="activity-time">2 hours ago</span>
              </div>
            </div>
            <div className="activity-item">
              <span className="activity-icon">üì¶</span>
              <div className="activity-details">
                <p>Order #1234 has been shipped</p>
                <span className="activity-time">5 hours ago</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
