import React, { useState, useEffect } from 'react';
import ProductCard from '../../components/ProductCard/ProductCard';

const Men = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMenProducts();
  }, []);

  const fetchMenProducts = async () => {
    try {
      // Fetch products for category 'men' from backend
      const res = await fetch('http://localhost:5000/api/products/category/men');
      const data = await res.json(); // assuming response is { products: [...] }
      setProducts(data.products || []);
    } catch (error) {
      console.error("Error fetching men's products:", error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div style={styles.loading}>Loading men's collection...</div>;
  }

  if (!products.length) {
    return <div style={styles.loading}>No products found in this category.</div>;
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Men's Collection</h1>
        <p style={styles.subtitle}>Discover our latest men's fashion and accessories</p>
      </div>

      <div style={styles.productsGrid}>
        {products.map((product) => (
          <ProductCard
            key={product._id}
            product={{
              ...product,
              images: product.images.map(img => ({
                url: `http://localhost:5000${img.url}`, // prepend backend URL
                alt: img.alt
              }))
            }}
          />
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '1400px',
    margin: '0 auto',
    padding: '2rem',
  },
  header: {
    textAlign: 'center',
    marginBottom: '3rem',
  },
  title: {
    fontSize: '2.5rem',
    color: '#333',
    marginBottom: '0.5rem',
  },
  subtitle: {
    fontSize: '1.1rem',
    color: '#666',
    maxWidth: '600px',
    margin: '0 auto',
  },
  productsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
    gap: '2rem',
  },
  loading: {
    textAlign: 'center',
    padding: '4rem',
    fontSize: '1.2rem',
  },
};

export default Men;
