import React from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

// Temporary fallback - remove when AuthContext is properly set up
const useAuthFallback = () => {
  return {
    user: {
      id: 1,
      name: 'Admin User', 
      email: 'admin@example.com',
      role: 'admin' // Change to 'user' to test non-admin view
    },
    loading: false
  };
};

const Navbar = () => {
  // Try to use AuthContext, fallback to demo data if not available
  let auth;
  try {
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const { useAuth } = require('../../contexts/AuthContext');
    auth = useAuth();
  } catch (error) {
    auth = useAuthFallback();
  }

  const { user, loading } = auth;

  if (loading) {
    return <nav className="navbar">Loading...</nav>;
  }

  return (
    <nav className="navbar">
      <div className="nav-container">
        <Link to="/" className="nav-logo">
          <img src="/logo.png" alt="" className="logo" />
          The Digital Bazar
        </Link>
        <ul className="nav-menu">
          <li className="nav-item">
            <Link to="/" className="nav-link">
              Home
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/men" className="nav-link">
              Men
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/women" className="nav-link">
              Women
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/kids" className="nav-link">
              Kids
            </Link>
          </li>
          
          {/* Admin Link - Only shown to admin users */}
          {user?.role === 'admin' && (
            <li className="nav-item">
              <Link to="/admin" className="nav-link">
                <span className="nav-icon">‚öôÔ∏è</span>
                <span className="nav-label">Admin</span>
              </Link>
            </li>
          )}
        </ul>
        <div className="nav-icons">
          <button className="icon-btn" aria-label="Search">
            <span className="icon">üîç</span>
          </button>
          <button className="icon-btn" aria-label="Wishlist">
            <span className="icon">‚ù§Ô∏è</span>
          </button>
          <button className="icon-btn" aria-label="Cart">
            <span className="icon">üõí</span>
            <span className="cart-count">0</span>
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
