import React, { useState, useEffect } from 'react';
import ProductCard from '../../components/ProductCard/ProductCard';

const Kids = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchKidsProducts();
  }, []);

  const fetchKidsProducts = async () => {
    try {
      // Mock kids' products - replace with actual API call
      const kidsProducts = [
        {
          _id: '13',
          name: 'Kids Backpack',
          images: ['https://via.placeholder.com/300x300/20c997/ffffff?text=Kids+Backpack'],
          description: 'Durable and colorful backpack for kids',
          price: 39.99,
          category: 'Kids',
          brand: 'Adidas',
          rating: 4.2,
          numReviews: 15,
          countInStock: 20
        },
        {
          _id: '14',
          name: 'Kids T-Shirt',
          images: ['https://via.placeholder.com/300x300/17a2b8/ffffff?text=Kids+T-Shirt'],
          description: 'Comfortable cotton t-shirt with fun prints',
          price: 14.99,
          category: 'Kids',
          brand: 'Carter\'s',
          rating: 4.5,
          numReviews: 42,
          countInStock: 60
        },
        {
          _id: '15',
          name: 'Kids Shoes',
          images: ['https://via.placeholder.com/300x300/f8f9fa/333333?text=Kids+Shoes'],
          description: 'Lightweight and comfortable shoes for kids',
          price: 34.99,
          category: 'Kids',
          brand: 'Nike',
          rating: 4.3,
          numReviews: 28,
          countInStock: 35
        },
        {
          _id: '16',
          name: 'Kids Toy Set',
          images: ['https://via.placeholder.com/300x300/ffc107/333333?text=Kids+Toys'],
          description: 'Educational toy set for children 3+ years',
          price: 29.99,
          category: 'Kids',
          brand: 'Fisher-Price',
          rating: 4.6,
          numReviews: 67,
          countInStock: 45
        },
        {
          _id: '17',
          name: 'Kids Winter Jacket',
          images: ['https://via.placeholder.com/300x300/007bff/ffffff?text=Kids+Jacket'],
          description: 'Warm winter jacket with waterproof material',
          price: 49.99,
          category: 'Kids',
          brand: 'Columbia',
          rating: 4.4,
          numReviews: 23,
          countInStock: 25
        },
        {
          _id: '18',
          name: 'Kids Story Books',
          images: ['https://via.placeholder.com/300x300/6f42c1/ffffff?text=Kids+Books'],
          description: 'Set of 5 illustrated story books for children',
          price: 24.99,
          category: 'Kids',
          brand: 'Disney',
          rating: 4.7,
          numReviews: 89,
          countInStock: 50
        }
      ];

      setProducts(kidsProducts);
    } catch (error) {
      console.error('Error fetching kids\' products:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div style={styles.loading}>Loading kids collection...</div>;
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Kids Collection</h1>
        <p style={styles.subtitle}>Find adorable and comfortable items for your little ones</p>
      </div>

      <div style={styles.productsGrid}>
        {products.map(product => (
          <ProductCard key={product._id} product={product} />
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '1400px',
    margin: '0 auto',
    padding: '2rem'
  },
  header: {
    textAlign: 'center',
    marginBottom: '3rem'
  },
  title: {
    fontSize: '2.5rem',
    color: '#333',
    marginBottom: '0.5rem'
  },
  subtitle: {
    fontSize: '1.1rem',
    color: '#666',
    maxWidth: '600px',
    margin: '0 auto'
  },
  productsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
    gap: '2rem'
  },
  loading: {
    textAlign: 'center',
    padding: '4rem',
    fontSize: '1.2rem'
  }
};

export default Kids;
