import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { adminProductAPI } from '../../services/adminAPI';
import { useAuth } from '../../context/AuthContext';
import './Admin.css';

const AddProduct = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    originalPrice: '',
    category: 'men',
    subcategory: '',
    brand: '',
    inventory: '',
    sku: '',
    sizes: [],
    colors: [],
    features: [''],
    isFeatured: false,
    isActive: true
  });

  const categories = {
    men: ['Shirts', 'Pants', 'Shoes', 'Accessories'],
    women: ['Dresses', 'Tops', 'Bottoms', 'Shoes'],
    kids: ['T-Shirts', 'Pants', 'Shoes', 'Toys']
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleArrayChange = (field, index, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: prev[field].map((item, i) => i === index ? value : item)
    }));
  };

  const addArrayField = (field) => {
    setFormData(prev => ({
      ...prev,
      [field]: [...prev[field], '']
    }));
  };

  const removeArrayField = (field, index) => {
    setFormData(prev => ({
      ...prev,
      [field]: prev[field].filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Prepare data for API
      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        originalPrice: formData.originalPrice ? parseFloat(formData.originalPrice) : undefined,
        inventory: parseInt(formData.inventory),
        sizes: formData.sizes.filter(size => size.trim() !== ''),
        colors: formData.colors.filter(color => color.trim() !== '').map(color => ({
          name: color,
          code: '#000000'
        })),
        features: formData.features.filter(feature => feature.trim() !== ''),
        images: [{ url: 'https://images.unsplash.com/photo-1441986300917-64674bd600d8?w=500&h=500&fit=crop', alt: formData.name }]
      };

      await adminProductAPI.create(productData);
      alert('Product created successfully!');
      navigate('/admin/products');
    } catch (error) {
      console.error('Error creating product:', error);
      alert('Error creating product: ' + (error.response?.data?.message || 'Something went wrong'));
    } finally {
      setLoading(false);
    }
  };

  if (!user || user.role !== 'admin') {
    return (
      <div className="admin-container">
        <div className="access-denied">
          <h2>Access Denied</h2>
          <p>You need admin privileges to access this page.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-container">
      <div className="admin-header">
        <h1>Add New Product</h1>
        <button onClick={() => navigate('/admin/products')} className="btn btn-secondary">
          Back to Products
        </button>
      </div>

      <form onSubmit={handleSubmit} className="product-form">
        <div className="form-grid">
          <div className="form-group">
            <label>Product Name *</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
              placeholder="Enter product name"
            />
          </div>

          <div className="form-group">
            <label>SKU *</label>
            <input
              type="text"
              name="sku"
              value={formData.sku}
              onChange={handleChange}
              required
              placeholder="Unique product SKU"
            />
          </div>

          <div className="form-group">
            <label>Category *</label>
            <select name="category" value={formData.category} onChange={handleChange} required>
              <option value="men">Men</option>
              <option value="women">Women</option>
              <option value="kids">Kids</option>
            </select>
          </div>

          <div className="form-group">
            <label>Subcategory *</label>
            <select name="subcategory" value={formData.subcategory} onChange={handleChange} required>
              <option value="">Select subcategory</option>
              {categories[formData.category]?.map(subcat => (
                <option key={subcat} value={subcat}>{subcat}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Price ($) *</label>
            <input
              type="number"
              step="0.01"
              name="price"
              value={formData.price}
              onChange={handleChange}
              required
              placeholder="0.00"
            />
          </div>

          <div className="form-group">
            <label>Original Price ($)</label>
            <input
              type="number"
              step="0.01"
              name="originalPrice"
              value={formData.originalPrice}
              onChange={handleChange}
              placeholder="0.00"
            />
          </div>

          <div className="form-group">
            <label>Brand *</label>
            <input
              type="text"
              name="brand"
              value={formData.brand}
              onChange={handleChange}
              required
              placeholder="Brand name"
            />
          </div>

          <div className="form-group">
            <label>Inventory *</label>
            <input
              type="number"
              name="inventory"
              value={formData.inventory}
              onChange={handleChange}
              required
              placeholder="0"
            />
          </div>
        </div>

        <div className="form-group full-width">
          <label>Description *</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
            rows="4"
            placeholder="Product description..."
          />
        </div>

        <div className="form-group full-width">
          <label>Sizes</label>
          <div className="array-fields">
            {formData.sizes.map((size, index) => (
              <div key={index} className="array-field">
                <input
                  type="text"
                  value={size}
                  onChange={(e) => handleArrayChange('sizes', index, e.target.value)}
                  placeholder="Size (e.g., S, M, L)"
                />
                <button type="button" onClick={() => removeArrayField('sizes', index)}>×</button>
              </div>
            ))}
            <button type="button" onClick={() => addArrayField('sizes')} className="btn btn-sm">
              + Add Size
            </button>
          </div>
        </div>

        <div className="form-group full-width">
          <label>Colors</label>
          <div className="array-fields">
            {formData.colors.map((color, index) => (
              <div key={index} className="array-field">
                <input
                  type="text"
                  value={color}
                  onChange={(e) => handleArrayChange('colors', index, e.target.value)}
                  placeholder="Color name"
                />
                <button type="button" onClick={() => removeArrayField('colors', index)}>×</button>
              </div>
            ))}
            <button type="button" onClick={() => addArrayField('colors')} className="btn btn-sm">
              + Add Color
            </button>
          </div>
        </div>

        <div className="form-group full-width">
          <label>Features</label>
          <div className="array-fields">
            {formData.features.map((feature, index) => (
              <div key={index} className="array-field">
                <input
                  type="text"
                  value={feature}
                  onChange={(e) => handleArrayChange('features', index, e.target.value)}
                  placeholder="Product feature"
                />
                <button type="button" onClick={() => removeArrayField('features', index)}>×</button>
              </div>
            ))}
            <button type="button" onClick={() => addArrayField('features')} className="btn btn-sm">
              + Add Feature
            </button>
          </div>
        </div>

        <div className="form-checkboxes">
          <label className="checkbox-label">
            <input
              type="checkbox"
              name="isFeatured"
              checked={formData.isFeatured}
              onChange={handleChange}
            />
            Featured Product
          </label>

          <label className="checkbox-label">
            <input
              type="checkbox"
              name="isActive"
              checked={formData.isActive}
              onChange={handleChange}
            />
            Active Product
          </label>
        </div>

        <div className="form-actions">
          <button type="submit" disabled={loading} className="btn btn-primary">
            {loading ? 'Creating Product...' : 'Create Product'}
          </button>
          <button type="button" onClick={() => navigate('/admin/products')} className="btn btn-secondary">
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddProduct;
