// pages/Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const featuredProducts = [
    { id: 1, name: 'Summer T-Shirt', price: '$29.99', image: '/api/placeholder/300/300', category: 'men' },
    { id: 2, name: 'Elegant Dress', price: '$49.99', image: '/api/placeholder/300/300', category: 'women' },
    { id: 3, name: 'Kids Sneakers', price: '$39.99', image: '/api/placeholder/300/300', category: 'kids' },
  ];

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1>Summer Collection 2024</h1>
          <p>Discover the latest trends in fashion</p>
          <Link to="/women" className="cta-button">Shop Now</Link>
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories">
        <h2>Shop by Category</h2>
        <div className="category-grid">
          <Link to="/men" className="category-card">
            <div className="category-image men-bg"></div>
            <h3>Men</h3>
          </Link>
          <Link to="/women" className="category-card">
            <div className="category-image women-bg"></div>
            <h3>Women</h3>
          </Link>
          <Link to="/kids" className="category-card">
            <div className="category-image kids-bg"></div>
            <h3>Kids</h3>
          </Link>
        </div>
      </section>

      {/* Featured Products */}
      <section className="featured-products">
        <h2>Featured Products</h2>
        <div className="products-grid">
          {featuredProducts.map(product => (
            <div key={product.id} className="product-card">
              <div className="product-image">
                <img src={product.image} alt={product.name} />
              </div>
              <h3>{product.name}</h3>
              <p className="price">{product.price}</p>
              <button className="add-to-cart">Add to Cart</button>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
